package PolyMorphism;

public class Methid_overloading {

	public static int sum(int a, int b) {
		
		return a+b;
	}
	
	public static double sum(double a, double b) {
		
		return a+b;
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		sum(1,2);
		
	}

}
